import React, { Component } from "react";

import { BrowserRouter as Router, Route } from "react-router-dom"; //React-Router

import Todos from "./components/Todos";
import Header from "./components/layout/Header";
import AddTodo from "./components/AddTodo";
import { v4 as uuidv4 } from "uuid";
import "./App.css";
import About from "./components/pages/About";

class App extends Component {
  state = {
    //state is an object
    todos: [
      {
        id: uuidv4(),
        title: "Eat healthy food",
        completed: false,
      },
      {
        id: uuidv4(),
        title: "Read a book",
        completed: false,
      },
      {
        id: uuidv4(),
        title: "Exercise at home",
        completed: false,
      },
      {
        id: uuidv4(),
        title: "Solve leetcode questions",
        completed: false,
      },
    ],
  };

  markComplete = (id) => {
    // console.log(id);
    this.setState({
      todos: this.state.todos.map((todo) => {
        if (todo.id === id) {
          todo.completed = !todo.completed; //we do this to toggle orelse it will always remain true/false
        }
        return todo;
      }),
    });
  };

  //but after refresh we will get all items again
  delTodo = (id) => {
    //console.log(id);
    this.setState({
      todos: [...this.state.todos.filter((todo) => todo.id !== id)],
    });
    //     if (todo.id === id) {
    //       todo.completed = !todo.completed; //we do this to toggle orelse it will always remain true/false
    //     }
    //     return todo;
    //   }),
    // });
  };

  // Add Todo
  addTodo = (title) => {
    //console.log(title);
    const newTodo = {
      id: 4,
      title,
      completed: false,
    };
    this.setState({
      todos: [...this.state.todos, newTodo],
    });
  };

  render() {
    return (
      <Router>
        <div className="App">
          <div className="container">
            <Header />
            <Route
              path="/"
              render={(props) => (
                <React.Fragment>
                  <AddTodo addTodo={this.addTodo} />
                  <Todos
                    todos={this.state.todos}
                    markComplete={this.markComplete}
                    delTodo={this.delTodo}
                  />
                </React.Fragment>
              )}
            />
            <Route path="/about" component={About} />
          </div>
        </div>
      </Router>
    );
  }
}

export default App;
